import React, { useState } from "react";
import "./app.css";
import Card from "./Card";
import Filter from "./Filter";
import Modal from "./Modal";
import Navbar from "./Navbar";


const App = () => {
  const [filter, setFilter] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [names, setName] = useState([
    {
      name: "Peter",
      Age: 31,
      addrress: "Alexandria",
      Phone: "000000",
      checked: false,
    },
    {
      name: "John",
      Age: 21,
      addrress: "Alexandria",
      Phone: "000002",
      checked: false,
    },
    {
      name: "Lara",
      Age: 1,
      addrress: "Alexandria",
      Phone: "0000003",
      checked: false,
    },
  ]);

  const handleDelete = () => {
    return setName(names.filter((name) => name.checked === false));
  };

  const filterNames = (name) => {
    setFilter(name);
  }

  const namesHandler = () => {
    if (filter.length !== 0)
      return names.filter((el) => el.name.includes(filter))
    else
      return names;
  }

  return (
    <div>
       <Navbar handleDelete={handleDelete} setShowModal={setShowModal} filteration={filterNames} setFilter={setFilter}/>
      <Modal show={showModal} closeModal={() => setShowModal(false)}/>
      <Card names={namesHandler()} setName={setName} ></Card>

    </div>
  );
};

export default App;
