import React, { useState } from "react";
import "./app.css";
import Card from "./Card";
import Filter from "./Filter";

const App = () => {
  const [names, setName] = useState([
    {
      name: "Peter",
      Age: 31,
      addrress: "Alexandria",
      Phone: "000000",
      checked: false,
    },
    {
      name: "John",
      Age: 21,
      addrress: "Alexandria",
      Phone: "000002",
      checked: false,
    },
    {
      name: "Lara",
      Age: 1,
      addrress: "Alexandria",
      Phone: "0000003",
      checked: false,
    },
  ]);

  const handleDelete = () => {
    const cards = names.filter((name) => name.checked === false);
    console.log(cards);
    setName(cards);
  };

  function solution() {
     const a = [4, 0, 1, -2, 3];

    const b = [];
    b = a.map((a ,index) => {
      a[index - 1] + a[index] + a[index + 1]
    });
    console.log(b);
    return b;
  }

  return (
    <div>
      <Filter />
      <Card names={names} setName={setName}></Card>
      <button type="button" className="btn btn-primary btn-floating deletBtn">
        <i className="fas fa-trash-can" onClick={solution()}></i>
      </button>
    </div>
  );
};

export default App;
