import React, { useState } from "react";
import "./app.css";
import Card from "./Card";
import Filter from "./Filter";
import Modal from "./Modal";
import Button from "./Button";


const App = () => {
  const [filter, setFilter] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [names, setName] = useState([
    {
      name: "Peter",
      Age: 31,
      addrress: "Alexandria",
      Phone: "000000",
      checked: false,
    },
    {
      name: "John",
      Age: 21,
      addrress: "Alexandria",
      Phone: "000002",
      checked: false,
    },
    {
      name: "Lara",
      Age: 1,
      addrress: "Alexandria",
      Phone: "0000003",
      checked: false,
    },
  ]);

  const handleDelete = () => {
    return setName(names.filter((name) => name.checked === false));
  };

  const filterNames = (name) => {
    setFilter(name);
  }

  const namesHandler = () => {
    if (filter.length !== 0)
      return names.filter((el) => el.name.includes(filter))
    else
      return names;
  }

  return (
    <div>
      <div>
        <nav className="navbar navbar-light bg-dark">
          <div>
            <Button>
              <i className="fas fa-trash-can" onClick={handleDelete}></i>
            </Button>
            <Button type="button" className="btn btn-warning btn-floating deletBtn">
              <i className="fas fa-plus" onClick={() => setShowModal(true)}></i>
            </Button>
          </div>
        </nav>
      </div>
      <Modal show={showModal} closeModal={() => setShowModal(false)}/>
      <Filter filteration={filterNames} />
      <Card names={namesHandler()} setName={setName} ></Card>

    </div>
  );
};

export default App;
