import React ,{ useState , useRef}from "react";
import "./app.css";
import Card from "./Card";
import Filter from "./Filter";

const App = () => {
  const inputF = useRef(null);
  const [names, setName] = useState([
    {
      name: "Peter",
      Age: 31,
      addrress: "Alexandria",
      Phone: "000000",
    },
    {
      name: "John",
      Age: 21,
      addrress: "Alexandria",
      Phone: "000002",
    },
    {
      name: "Lara",
      Age: 1,
      addrress: "Alexandria",
      Phone: "0000003",
    },
  ]);

  const handleDelete = (e, index) => {
    setName((prevState) => {
      return prevState.filter((el, idx) => idx !== index);
    });
  };

  const testHandler =  (e) => {
    console.log(inputF.current.value);
    inputF.current.focus();
  } 
  return (
    <div>
      <Filter inputF={inputF} />
      <Card names={names} handleDelete={handleDelete}></Card>
    </div>
  );
};

export default App;
