import React from "react";
import SelectBtn from "./SelectBtn";

const Card = ({ names , setName }) => {


  const getSelectedIndex = (index) => {
    console.log(index);
  } 
 
      
 const cards = names.map(({ name, Age, addrress, Phone }, index) => (
          <div className="cardWrraper">
          <div className="card" key={index}>
            <p>{`Name : ${name}`}</p>
            <p>{`Age : ${Age}`}</p>
            <p>{`addrress : ${addrress}`}</p>
            <p>{`Phone : ${Phone}`}</p>
            
            <SelectBtn index={index} getSelectedIndex={getSelectedIndex} cards={names} setName={setName} />
          </div>
          </div>
        ));
        return <div></div>
};

export default Card;
