import React , {Fragment} from 'react'
import styles from './Modal.module.css'
import ReactDOM from 'react-dom';

const Backdrop = ({close , show}) => {
    return <div className={`${styles.backDrop} ${show ? styles.show : null }`}onClick={close}></div>
}

const Overlay =  ({show}) => {
    return (
     <div className={`${styles.overlay} ${show ? styles.showControl : null }`}>
     <Controller>
        <label htmlFor='name'>NAME</label>
        <input type='text' placeholder='set a name '></input>
      </Controller>
     </div>
     )
}

const Modal = ({show , closeModal}) => {
  return (
    show && (
    <Fragment>
      {ReactDOM.createPortal(
         <Fragment>
            <Backdrop  close={closeModal} show={show}/>
            <Overlay  show={show}/>
         </Fragment>, 
          document.getElementById('modal') 
      )
      }
    </Fragment>
    )
  )
}

export default Modal;
