import React ,{ useState }from "react";
import "./app.css";
import Card from "./Card";


const App = () => {
  const [names, setName] = useState([
    {
      name: "Peter",
      Age: 31,
      addrress: "Alexandria",
      Phone: "000000",
    },
    {
      name: "John",
      Age: 21,
      addrress: "Alexandria",
      Phone: "000002",
    },
    {
      name: "Lara",
      Age: 1,
      addrress: "Alexandria",
      Phone: "0000003",
    },
  ]);

  const handleDelete = (e, index) => {
    setName((prevState) => {
      return prevState.filter((el, idx) => idx !== index);
    });
  };
  return (
    <div>
      <Card names={names} deleteFunc={handleDelete}></Card>

    </div>
  );
};

export default App;
