    setSelect(!selected);
    cards[index].checked= !selected;
    
  };
  return (
    <div>
    <div className="cardWrraper">
      {names.map(({ name, Age, addrress, Phone }, index) => (
  
          <div className="card" key={index}>
            <p>{`Name : ${name}`}</p>
            <p>{`Age : ${Age}`}</p>
            <p>{`addrress : ${addrress}`}</p>
            <p>{`Phone : ${Phone}`}</p>

            <SelectBtn index={index}  cards={names} setName={setName} />
          </div>
     
      ))}
         </div>
    </div>
  );
};

export default Card;
