import React from 'react'

const DeleteBtn = ({names , getSelectedIndex}) => {

   const {index} = getSelectedIndex();

  const handleDelete = (e, index) => {
      return names.filter((el, idx) => idx !== index);
  };
  
 
  return (
    <div>
       <button type="button" className="btn btn-primary btn-floating deletBtn">
              <i
               className="fas fa-trash-can"
               onClick={(e) => handleDelete(e , index)}
                 ></i>
               </button>
    </div>
  )
}

export default DeleteBtn
