    setName(cards);
  };

  return (
    <div>
      <button type="button" className="btn btn-primary btn-floating deletBtn">
        <i
          className="fas fa-trash-can"
          onClick={(e, index) => handleDelete()}
        ></i>
      </button>
    </div>
  );
};

