import React from "react";
import DeleteBtn from "./DeleteBtn";
import SelectBtn from "./SelectBtn";

const Card = ({ names , setName}) => {


  const getSelectedIndex = (index) => {
    console.log(index);
  } 
  return (
    <div>
      <div className="cardWrraper">
        {names.map(({ name, Age, addrress, Phone }, index) => (
          <div className="card" key={index}>
            <p>{`Name : ${name}`}</p>
            <p>{`Age : ${Age}`}</p>
            <p>{`addrress : ${addrress}`}</p>
            <p>{`Phone : ${Phone}`}</p>
            
            <SelectBtn index={index} getSelectedIndex={getSelectedIndex} />
          </div>
        ))}
        
      </div>
          
    </div>
  );
};

export default Card;
