import React ,{ useState }from "react";

const SelectBtn = ({index , setName , cards}) => {
  const [selected, setSelect] = useState(false);

  const handleSelect = (e) => {
    setSelect(!selected);
    cards[index].checked= !selected;
    console.log
  };
  return (
    <div>
      <button  className="selectBtn" onClick={handleSelect}>
        <i index={index}  className={selected ?  "fas fa-circle-check" : "far fa-circle"}></i>
      </button>
    </div>
  );
};

export default SelectBtn;
